# schemas.py
# ðŸ“¦ Purpose: Define request and response data structures using Pydantic

from pydantic import BaseModel, EmailStr

# ðŸ”¹ Used when client sends data to create a new user
# Note id is not included here because it's auto-generated by the DB

class UserCreate(BaseModel):
    name: str
    email: EmailStr
    age: int

# ðŸ”¹ Used when sending user data back to the client
class UserResponse(BaseModel):
    id: int
    name: str
    email: EmailStr
    age: int

    # Tells FastAPI to convert DB model â†’ response model
    class Config:
        orm_mode = True
